import React from 'react'

const Form = ({movie, setMovie}) => {

    const handleChange = (e) => {
        setMovie({
            [e.target.name]: e.target.value
        })

        console.log(movie)
    }

    return ( 
        <form>
            <div className="mb-2">
                <label htmlFor='title' className='form-label'>Title</label>
                <input name='title' onChange={handleChange} type="text" id='title' className='form-control' />
            </div>
            <div className="mb-2">
                <label htmlFor='date' className='form-label'>Date</label>
                <input name='date' onChange={handleChange}  type="date" id='date' className='form-control' />
            </div>
            <div className="mb-2">
                <label htmlFor='Type' className='form-label'>Type</label>
                <input name='type' onChange={handleChange}  type="text" id='Type' className='form-control' />
            </div>
            <button className='btn btn-primary'>Submit</button>
        </form>
     );
}
 
export default Form;
